import React from "react";

const SocialShare = [
  {
    iconName: "fa fa-github",
    link: "https://github.com/arifbillah30",
    name: "GitHub"
  },
  { 
    iconName: "fa fa-linkedin", 
    link: "https://www.linkedin.com/in/arifbillah30",
    name: "LinkedIn"
  },
  {
    iconName: "fa fa-twitter",
    link: "https://twitter.com/arifbillah30",
    name: "Twitter"
  },
  { 
    iconName: "fa fa-graduation-cap", 
    link: "https://www.researchgate.net/profile/Arif-Billah",
    name: "ResearchGate"
  },
];

const Social = ({ align = 'center' }) => {
  return (
    <div style={{ 
      display: 'flex', 
      justifyContent: align === 'center' ? 'center' : 'flex-start', 
      gap: '25px', 
      marginTop: '30px',
      marginBottom: '30px',
      flexWrap: 'wrap',
      alignItems: 'flex-start'
    }}>
      {SocialShare.map((val, i) => (
        <a 
          key={i}
          href={val.link} 
          target="_blank" 
          rel="noreferrer"
          className="social-link"
          style={{ 
            textDecoration: 'none', 
            color: '#999',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            gap: '6px',
            transition: 'all 0.3s ease',
            padding: '5px',
            minWidth: '70px'
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.color = '#ffb400';
            e.currentTarget.style.transform = 'translateY(-2px)';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.color = '#999';
            e.currentTarget.style.transform = 'translateY(0)';
          }}
        >
          <i className={val.iconName} style={{ fontSize: '20px' }}></i>
          <span style={{ fontSize: '10px', fontWeight: '500', textTransform: 'uppercase', letterSpacing: '0.5px' }}>{val.name}</span>
        </a>
      ))}
    </div>
  );
};

export default Social;
